                                                                                                                                                                                                                                                                                                                                                                                                                                                                              # The variables 'green', 'black', and 'red' contain the number of pockets for each color
                                                                                                                                                                                                                                                                                                                                                                                                                                                                              green <- 2
                                                                                                                                                                                                                                                                                                                                                                                                                                                                              black <- 18
